    def __init__(self, img_path):
        self.__im = Image.open(img_path)
        #保存切割后的图像信息
        self.__im_list = []
        self.__yzm = ''
        #图像切割
        self.__cutting()
        #set验证码数字
        self.__setvalues()
 
    def __cutting(self):
        '''
        切割图像，
        按照列扫描整张图像，按照图像上的数字信息，切割成N份图像
        '''
        box = (25, 9, 115,36)
        #裁剪图像，保留有效区域
        im = self.__im.crop(box)
      
        w,h = im.size
        # 二值化
        for x in range(w):
            for y in range(h):
                if im.getpixel((x, y)) == (0,0,0):
                    continue
                im.putpixel((x,y), (255,255,255))
        # 图像切割
        x_end_list = []
        x_start_list = []
        #前一列上是否有黑点
        pre_line = False
        for x in range(w):
            #当前扫描的列上是否有黑点
            cur_line = False
            for y in range(h):
                # 存在黑点,
                if im.getpixel((x, y)) == (0,0,0):
                    cur_line = True
                    break
            #若当前列不存在黑点,前一列存在黑点,则当前列的x坐标为小长方形的纵向结束坐标
            if (cur_line == False and pre_line):
                x_end_list.append(x)
                pre_line = cur_line
                continue
            #若当前列存在黑点,且前一列不存在黑点时,则当前的x为小长方形的纵向起始坐标
            if (cur_line and pre_line == False):
                x_start_list.append(x)
                pre_line = cur_line
                continue
            pre_line = cur_line
          
        if len(x_end_list) < 4:
            x_end_list.append(w)
        for i,j in zip(x_start_list, x_end_list):
            self.__im_list.append(im.crop((i, 0, j, h)))
 
    def __setvalues(self):
        for img in self.__im_list:
            data_list = []
            for x in list(img.getdata()):
                n = 1 if(x == 255 or x == (255,255,255)) else 0
                data_list.append(n)
            for i in range(10):
                if data_list == self.__num_datas[i]:
                    self.__yzm = self.__yzm+str(i)
                    break
     
    def getyzm(self):
        return self.__yzm
 
    def __denosing(image):
        '''
        对图像去噪
        '''
        im = image
        w,h = im.size
        # 二值化
        im = im.convert('1')    
        for x in range(w):
            for y in range(h):
                # 若是白点，则不处理
                if im.getpixel((x, y)) == 255:
                    continue
                # 图像四边的点全填充为白色
                if x==0 or y==0 or x==w-1 or y==h-1:
                    im.putpixel((x, y), 255)
                    continue
                # 记录当前点周围8个点中黑点数
                black_point = 0
                if im.getpixel((x-1,y-1)) == 0:
                    black_point = black_point+1
                if im.getpixel((x,y-1)) == 0:
                    black_point = black_point+1
                if im.getpixel((x+1,y-1)) == 0:
                    black_point = black_point+1
                if im.getpixel((x-1,y)) == 0:
                    black_point = black_point+1
                if im.getpixel((x+1,y)) == 0:
                    black_point = black_point+1
                if im.getpixel((x-1,y+1)) == 0:
                    black_point = black_point+1
                if im.getpixel((x,y+1)) == 0:
                    black_point = black_point+1
                if im.getpixel((x+1,y+1)) == 0:
                    black_point = black_point+1
                # 若周围的黑点数小于3个，则认为当前点应该为白点
                if black_point <= 2:
                    im.putpixel((x, y), 255)
        return im;
